//.2.Now, solve the above question using assignment operators (eg. +=, -=, *=).
package assignment;

public class AssignmentOperators {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=2345;
		number+=8;
		number/=3;
		number%=5;
		number*=5;
		System.out.println(number);

	}
}
